import BigNumber from 'bignumber.js'
import React, { useCallback, useState } from 'react'
import styled from 'styled-components'
import { Button, IconButton, useModal, AddIcon, Image, Flex, MinusIcon, Link } from '@pancakeswap-libs/uikit'
import { useWallet } from '@binance-chain/bsc-use-wallet'
import UnlockButton from 'components/UnlockButton'
import { useERC20 } from 'hooks/useContract'
import { useSousApprove, useSousApproveBurn } from 'hooks/useApprove'
import useI18n from 'hooks/useI18n'
import { useSousStake, useSousStakeBurn } from 'hooks/useStake'
import { useSousUnstake, useSousUnstakeBurn } from 'hooks/useUnstake'
import useBlock from 'hooks/useBlock'
import { getBalanceNumber } from 'utils/formatBalance'
import { useSousHarvestBurn } from 'hooks/useHarvest'
import Balance from 'components/Balance'
import { QuoteToken, PoolCategory } from 'config/constants/types'
import { Pool2 } from 'state/types'
import { FaAngleRight, FaHistory } from 'react-icons/fa'
import ExpandableSectionButton from 'components/ExpandableSectionButton'
import Container from 'components/layout/Container'
import DepositModal from './DepositModal'
import WithdrawModal from './WithdrawModal'
import CompoundModal from './CompoundModal'
import Card from './Card'
import HarvestButton from './HarvestButton'
import CardFooter from './CardFooter'



interface PoolWithApy extends Pool2 {
  apy: BigNumber
}

interface HarvestProps {
  pool2: PoolWithApy
}

const DCard = styled.div`
background-image: linear-gradient(to right, #2E3646, #3B4557 , #2B3344);
  border-radius: 15px;
  flex-direction: column;
  justify-content: space-around;
  padding: 25px;
  position: center;
  text-align: center;

  border:0px solid #fff;
  box-shadow: 0px 0px 0px #ccc;
`

const CCARD = styled.div`
background: #1E2129;
border-radius: 10px;
flex-direction: column;
justify-content: space-around;
padding: 25px;
position: center;
text-align: center;
`

const Wrapper = styled(Flex)`
  svg {
    margin-right: 0.25rem;
  }
`

const StyledBtn = styled.button`
  -webkit-box-align: center;
  align-items: center;
  background-color: #2E3543 !important;
  border: 1px;
  border-style: solid !important;
  border-color: #2E3543 !important;
  border-radius: 8px;
  color: #ffff;
  font-size: 15px;
  font-weight: 400;
  width: 100%;
  display: inline-flex;
  margin-top: 18px;
  min-height: 18px;
  max-height: 30px;
  max-width: 128px;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;

  `
const StyledClaim = styled.button`
  -webkit-box-align: center;
  align-items: center;
  background-color: #2E3543 !important;
  border: 1px;
  border-style: solid !important;
  border-color: #2E3543 !important;
  border-radius: 8px;
  color: #ffff;
  font-size: 15px;
  font-weight: 400;
  width: 100%;
  display: inline-flex;
  margin-top: 18px;
  min-height: 18px;
  max-height: 30px;
  max-width: 128px;
  padding: 20px;
  `

const ClaimBtn = styled.button`
    -webkit-box-align: center;
    align-items: center;
    margin-top: 18px;
    background-color: rgba(0, 0, 0,0) !important;
    border: 1px;
    border-style: solid !important;
    border-color: #ffff !important;
    border-radius: 10px;
    color: #ffff;
    font-size: 15px;
    font-weight: 400;
    width: 100%;
    display: inline-flex;
    min-height: 18px;
    max-height: 30px;
    max-width: 80px;
    padding: 20px;
  `

  const ClaimBtn2 = styled.button`
    -webkit-box-align: center;
    align-items: center;
    margin-top: 18px;
    background-color: rgba(0, 0, 0,0) !important;
    border: 1px;
    border-style: solid !important;
    border-color: #ffff !important;
    border-radius: 10px;
    color: #ffff;
    font-size: 15px;
    font-weight: 400;
    width: 100%;
    display: inline-flex;
    min-height: 18px;
    max-height: 30px;
    max-width: 180px;
    padding: 20px;
  `


  const ExpandingWrapper = styled.div<{ expanded: boolean }>`
  height: ${(props) => (props.expanded ? '100%' : '0px')};
  overflow: hidden;
`

const Quote = styled.p`
    font-size: 15px;
    font-weight: 500;
    margin-bottom: 0px;
    text-shadow: 0px 0px 5px #ccc;
`

const Rewards = styled.p`
    font-size: 15px;
    font-weight: 600;
    margin-bottom: 0px;
    text-shadow: 0px 0px 0px #ccc;
`

const Quote2 = styled.p`
    font-size: 15px;
    font-weight: 300;
    margin-bottom: 0px;
`

const Quote3 = styled.p`
    font-size: 15px;
    font-weight: 700;
    margin-bottom: 0px;
    text-shadow: 1px 1px 10px #ccc;
    margin-top: 7px;
`

const QuoteBox = styled.p`
  font-size: 15px;
  font-weight: 700;
  margin-bottom: 0px;
  text-shadow: 1px 1px 10px #ccc;
  box-shadow: 0px 0px 0px #fff;
  box-color: #fff
`

const DashboardPage2 = styled(Container)`
  min-height: calc(1vh - 64px);
  padding-top: 16px;
  padding-bottom: 6px;
  padding: 5px;
  max-width: 770px;

  ${({ theme }) => theme.mediaQueries.sm} {
    padding-top: 24px;
    padding-bottom: 24px;
  }

  ${({ theme }) => theme.mediaQueries.lg} {
    padding-top: 21px;
    padding-bottom: 32px;
  }
`


const Divider = styled.div`
  background-color: #7B7B7B;
  height: 1px;
  margin-left: auto;
  margin-right: auto;
  margin-top: 25px;
  margin-bottom: 15px;
  width: 100%;
`


const PoolCard: React.FC<HarvestProps> = ({ pool2 }) => {
  const {
    sousId,
    image,
    tokenName,
    stakingTokenName,
    stakingTokenAddress,
    projectLink,
    harvest,
    apy,
    tokenDecimals,
    poolCategory,
    totalStaked,
    startBlock,
    endBlock,
    isFinished,
    isDepositFinished,
    userData,
    stakingLimit,
    tokenPoolAddress,
    quoteTokenPoolAddress,
    notFinished,
    lockBlock,
  } = pool2
  const isBnbPool = poolCategory === PoolCategory.BINANCE
  const TranslateString = useI18n()
  const stakingTokenContract = useERC20(stakingTokenAddress)
  const { account } = useWallet()
  const block = useBlock()
  const { onApprove } = useSousApproveBurn(stakingTokenContract, sousId)
  const { onStake } = useSousStakeBurn(sousId, isBnbPool)
  const { onUnstake } = useSousUnstakeBurn(sousId)
  const { onReward } = useSousHarvestBurn(sousId, isBnbPool)
  const [requestedApproval, setRequestedApproval] = useState(false)
  const [pendingTx, setPendingTx] = useState(false)
  const allowance = new BigNumber(userData?.allowance || 0)
  const stakingTokenBalance = new BigNumber(userData?.stakingTokenBalance || 0)
  const stakedBalance = new BigNumber(userData?.stakedBalance || 0)
  const earnings = new BigNumber(userData?.pendingReward || 0)
  const daysRemaining = block > startBlock ? (endBlock - block)*2*0.000277778*0.0416667 : (endBlock - startBlock)*2*0.000277778*0.0416667
  const [showExpandableSection, setShowExpandableSection] = useState(false)
  const blocksDepositFinished = Math.max(lockBlock - block, 0)
  const blocksRemaining = Math.ceil(endBlock - block).toLocaleString('en-us',{ maximumFractionDigits: 1 })
  const isOldSyrup = stakingTokenName === QuoteToken.SYRUP
  const accountHasStakedBalance = stakedBalance?.toNumber() > 0
  const needsApproval = !accountHasStakedBalance && !allowance.toNumber() && !isBnbPool
  const isCardActive = isFinished && accountHasStakedBalance

  const convertedLimit = new BigNumber(stakingLimit).multipliedBy(new BigNumber(10).pow(tokenDecimals))
  const [onPresentDeposit] = useModal(
    <DepositModal
      max={stakingLimit && stakingTokenBalance.isGreaterThan(convertedLimit) ? convertedLimit : stakingTokenBalance}
      onConfirm={onStake}
      tokenName={stakingLimit ? `${stakingTokenName} (${stakingLimit} max)` : stakingTokenName}
    />,
  )

  const handleApprove = useCallback(async () => {
    try {
      setRequestedApproval(true)
      const txHash = await onApprove()
      // user rejected tx or didn't go thru
      if (!txHash) {
        setRequestedApproval(false)
      }
    } catch (e) {
      console.error(e)
    }
  }, [onApprove, setRequestedApproval])

  const APR = apy && apy.toNumber().toLocaleString('en-us',{ maximumFractionDigits: 0 })
  const NetROI = apy && apy.div(365).times(daysRemaining).toNumber().toLocaleString('en-us',{ maximumFractionDigits: 1 })
  const dailyROI = apy && apy.div(365).toNumber().toLocaleString('en-us',{ maximumFractionDigits: 1 })
  const FiveDayROI = apy && apy.div(365).times(5).toNumber().toLocaleString('en-us',{ maximumFractionDigits: 1 })
  const TVL = pool2.tvl && pool2.tvl.toNumber().toLocaleString('en-us',{ maximumFractionDigits: 0 })
  const NetProfitNumber = apy && apy.div(365).times(daysRemaining).minus(100).toNumber()
  const Profit = NetProfitNumber && NetProfitNumber.toLocaleString('en-us',{ maximumFractionDigits: 1 })
  // console.log(pool2.sousId, pool2.tvl && pool2.tvl.toNumber())

  const ClaimableRVRS = getBalanceNumber(earnings, tokenDecimals).toLocaleString('en-us',{ maximumFractionDigits: 1 })
  const ForfeitedA = getBalanceNumber(stakedBalance).toLocaleString('en-us',{ maximumFractionDigits: 1 })

  return (

    <DashboardPage2>
    <Card isActive={isCardActive} isFinished={isFinished && sousId !== 0}>
      {sousId === 0 && <PoolFinishedSash />}

      <Wrapper justifyContent="space-between" alignItems="center" mb="0px" padding='15px' >

      <Flex flexDirection="column" alignItems="flex-end">
          <Quote>{tokenName} Bonding Pool</Quote>

        </Flex>

      </Wrapper>

      <DCard>

      <Wrapper justifyContent="space-between" alignItems="center" mb="0px">

          {notFinished && (
          <Flex flexDirection="column" alignItems='start' >
            <Quote>Current Net ROI</Quote>
            {NetProfitNumber && NetProfitNumber > 0 ? <Quote3>{Profit}%</Quote3> :  <Quote3>Sold Out</Quote3>}
          </Flex>)}

          {notFinished && (
          <Flex flexDirection="column" alignItems='start' >
            <Quote>Vesting</Quote>
            {daysRemaining > 0 ?
                <Quote3>{daysRemaining.toLocaleString('en-us',{ maximumFractionDigits: 1 })} Days</Quote3> :
                <Quote3>Ended</Quote3>}
          </Flex>)}

          {isFinished && (
          <Flex flexDirection="column" alignItems='start' >
            <QuoteBox><FaHistory/>Pool Ended </QuoteBox>
          </Flex>)}

          <Flex flexDirection="column" alignItems='start' >
            <Quote>TVB</Quote>
            <Quote3>${TVL}</Quote3>
          </Flex>

          <Flex justifyContent='right'>
            <ExpandableSectionButton onClick={() => setShowExpandableSection(!showExpandableSection)}/>
          </Flex>
        </Wrapper>
      </DCard>


        <ExpandingWrapper expanded={showExpandableSection}>
          <CCARD>

          <Flex justifyContent='space-between' marginTop='20px'>
              <Quote>Total Value Bonded</Quote>
              <Quote>${TVL}</Quote>
            </Flex>

            {notFinished && (
            <Flex justifyContent='space-between' marginTop='10px'>
              <Quote>Daily Returns</Quote>
              <Quote>{dailyROI}%</Quote>
            </Flex>)}


            {notFinished && (
            <Flex justifyContent='space-between' marginTop='10px'>
              <Quote>Current Net ROI</Quote>
              {NetProfitNumber && NetProfitNumber > 0 ? <Quote3>{Profit}%</Quote3> :  <Quote3>Sold Out</Quote3>}
            </Flex>)}

            {notFinished && (
            <Divider/>)}

            <Flex justifyContent='space-between' marginTop='25px'>
              <Quote> Your Forfeited Assets</Quote>
              <Quote>{ForfeitedA}</Quote>
            </Flex>


            <Flex marginTop='10px' justifyContent='space-between'>
              <Quote> Claimable RVRS</Quote>
              <Quote>{ClaimableRVRS}</Quote>
            </Flex>

            {notFinished && (
            <Flex justifyContent='space-between' marginTop='20px'>
              <Quote2>Once forfeited to the Reverseum, assets cannot be recovered.
                Net ROI is is a global indicator that shows the ROI if you would enter at THIS POINT IN TIME, thus over the course of the pool, it will go negative - this does not mean that you PERSONALLY will  necessarily make a loss.
                Rewards in RVRS are linearly vested for {daysRemaining.toLocaleString('en-us',{ maximumFractionDigits: 1 })} more days (Based on 2s block times)</Quote2>
            </Flex>)} 



            <StyledCardActions >

              {account && (needsApproval && !isOldSyrup ? (

                <div style={{ flex: 1 }}>
                  <StyledBtn 
                  style={{justifyContent:"center", maxWidth:'160px' }}
                  disabled={isFinished || isDepositFinished} 
                  onClick={handleApprove}
                  >
                    Enable Pools
                  </StyledBtn>
                </div>

                ) : ( <>

                <StyledActionSpacer/>


                  {!isOldSyrup && (
                    <StyledBtn 
                      style={{justifyContent:"center", maxWidth:'160px' }}
                      disabled={isFinished || isDepositFinished || NetProfitNumber && NetProfitNumber < 0}
                      onClick={onPresentDeposit}>
                      {NetProfitNumber > 0 ? 'Bond Assets' : 'Sold Out'}
                    </StyledBtn>)}

                  {account && harvest && !isOldSyrup && (
                    <ClaimBtn
                      style={{ marginLeft:'20px', justifyContent:"center" }}
                      disabled={!earnings.toNumber() || requestedApproval || pendingTx}
                      onClick={async () => {
                        setPendingTx(true)
                        await onReward()
                        setPendingTx(false)}}>
                      Claim
                    </ClaimBtn>)}

                    </>))}



              </StyledCardActions>
            </CCARD>
        </ExpandingWrapper>


        {/* {!isOldSyrup ? (
          <BalanceAndCompound>
            <Balance value={getBalanceNumber(earnings, tokenDecimals)} isDisabled={isFinished} />
            {sousId === 0 && account && harvest && (
              <HarvestButton
                disabled={!earnings.toNumber() || pendingTx}
                text={pendingTx ? TranslateString(999, 'Compounding') : TranslateString(999, 'Compound')}
                onClick={onPresentCompound}
              />

            )}
          </BalanceAndCompound>
        ) : (
          <OldSyrupTitle hasBalance={accountHasStakedBalance} />
        )}
        <Label isFinished={isFinished && sousId !== 0} text={TranslateString(330, `${tokenName} earned`)} />
        */}


        {/* <StyledDetails>
          <div style={{ flex: 1 }}>{TranslateString(736, 'APY')}:</div>
          {isFinished || isOldSyrup || !apy || apy?.isNaN() || !apy?.isFinite() ? (
            '-'
          ) : (
            <Balance fontSize="14px" isDisabled={isFinished} value={apy?.toNumber()} decimals={2} unit="%" />
          )}
        </StyledDetails> */}

    </Card>  
    </DashboardPage2>

  )

}

const PoolFinishedSash = styled.div`
  background-image: url('/images/pool-finished-sash.svg');
  background-position: top right;
  background-repeat: not-repeat;
  height: 135px;
  position: absolute;
  right: -24px;
  top: -24px;
  width: 135px;
`

const StyledCardActions = styled.div`
  display: flex;
  justify-content: center;
  margin: 16px 0;
  width: 100%;
  box-sizing: border-box;
`

const BalanceAndCompound = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: row;
`

const StyledActionSpacer = styled.div`
  height: ${(props) => props.theme.spacing[4]}px;
  width: ${(props) => props.theme.spacing[4]}px;
`

const StyledDetails = styled.div`
  display: flex;
  font-size: 14px;
`

export default PoolCard
